mapper.select:
select

from leave_bill


    and user_id = #{userId,jdbcType=INTEGER}


    and reason like  %#{reason,jdbcType=VARCHAR}%


    and leave_time = #{leaveTime,jdbcType=DATETIME}


    and status = #{status,jdbcType=TINYINT}


    and leave_time between #{startTime,jdbcType=DATETIME} and #{endTime,jdbcType=DATETIME}


limit #{((pageNum - 1) * pageSize)},#{pageSize}

select
coun(*)
from leave_bill


    and user_id = #{userId,jdbcType=INTEGER}


    and reason like  %#{reason,jdbcType=VARCHAR}%


    and leave_time = #{leaveTime,jdbcType=DATETIME}


    and status = #{status,jdbcType=TINYINT}


    and leave_time between #{startTime,jdbcType=DATETIME} and #{endTime,jdbcType=DATETIME}

mapper.update:
upDATETIME leave_bill
set user_id = #{userId,jdbcType=INTEGER},
  reason = #{reason,jdbcType=VARCHAR},
  leave_time = #{leaveTime,jdbcType=DATETIME},
  status = #{status,jdbcType=TINYINT}
where id = #{id,jdbcType=INTEGER}
